import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { environment } from '../../../../environments/environment';
import { HttpServiceWrapperService } from '../loanding/http-service-wrapper.service';
import { ApiResponse } from '../../Models/api-response.models';

@Injectable({
  providedIn: 'root'
})
export class ApiService<T, D> {

  constructor(
    protected http: HttpClient,
    protected wrapper: HttpServiceWrapperService
  ) { }
  urlBase = environment.URL + '/api';

  public ObtenerTodo(entidad: string): Observable<ApiResponse<D[]>> {
    return this.wrapper.handleRequest(this.http.get<ApiResponse<D[]>>(`${this.urlBase}/${entidad}`));
  }

  public ObtenerPorId(entidad: string, id: number): Observable<ApiResponse<D>> {
    return this.wrapper.handleRequest(this.http.get<ApiResponse<D>>(`${this.urlBase}/${entidad}/${id}`));
  }
  public ObtenerActivos(entidad: string): Observable<ApiResponse<D>> {
    return this.http.get<ApiResponse<D>>(`${this.urlBase}/${entidad}/active`);
  }
  public Crear(entidad: string, objeto: T) {
    return this.wrapper.handleRequest(this.http.post<ApiResponse<D>>(`${this.urlBase}/${entidad}`, objeto));
  }
  public update(entidad: string, data: T) : Observable<ApiResponse<D>>{
    return this.wrapper.handleRequest(this.http.put<ApiResponse<D>>(`${this.urlBase}/${entidad}/update/`, data));
  }
  public delete(entidad: string, id: number) {
    return this.wrapper.handleRequest(this.http.delete(`${this.urlBase}/${entidad}/${id}`));
  }
  public deleteLogic(entidad: string, id: number) {
    return this.wrapper.handleRequest(this.http.patch(`${this.urlBase}/${entidad}/${id}/toggle-active`, null));
  }

  loginWithGoogle(tokenId: string) {
    return this.wrapper.handleRequest(this.http.post<{ token: string }>(`${this.urlBase}/Auth/google`, { tokenId }));
  }
  exchangeCodeForToken(code: any): Observable<any> {
    return this.http.post(`${this.urlBase}/Auth/login-google-code`, { code });
  }

}

import { Injectable } from '@angular/core';
import { firstValueFrom, of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { AuthService } from '../Services/auth/auth-service.service';
import { TokenService } from '../Services/token/token.service';
import { UserStoreService } from '../Services/auth/user-store.service';

@Injectable({ providedIn: 'root' })
export class AuthBootstrap {
  constructor(
    private auth: AuthService,
    private tokens: TokenService,
    private userStore: UserStoreService
  ) {}

  async init(): Promise<void> {
    // Si hay accessToken al arrancar, intenta poblar el usuario
    const token = this.tokens.getAccessToken();
    if (!token) return;

    await firstValueFrom(
      this.auth.getMe().pipe(
        catchError(() => of(null)) // si falla, no rompas el arranque
      )
    ).then(u => {
      if (u) this.userStore.setUser(u.data);
      else this.userStore.clear();
    });
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { jwtDecode } from 'jwt-decode';
import Swal from 'sweetalert2';
import { getCookie, removeCookie, setCookie } from 'typescript-cookie'
import { environment } from '../../../../environments/environment';
import { Observable, switchMap, tap } from 'rxjs';
import { TokenService } from '../token/token.service';
import { RefreshRequest, RequestCode, RequestLogin, ResponseLogin, ResponseToken } from '../../Models/auth.models';
import { HttpServiceWrapperService } from '../loanding/http-service-wrapper.service';
import { UserMe } from '../../Models/security/user.models';
import { UserStoreService } from './user-store.service';
import { ApiResponse } from '../../Models/api-response.models';
@Injectable({
  providedIn: 'root'
})
export class AuthService {
  constructor(
    private http: HttpClient,
    private tokenService: TokenService,
    protected wrapper: HttpServiceWrapperService,
    private userStore: UserStoreService,
  ) { }


  urlBase = environment.URL + '/api';

  // Auth
  public login(credentials: RequestLogin) {
    debugger
    return this.wrapper.handleRequest(this.http.post<any>(`${this.urlBase}/Auth/login`, credentials))
      .pipe(
        tap(res => {
          // Guarda ambos tokens; refresh por defecto 7 días
          this.cachePendingEmail(credentials.email, res.data.userId)

          // this.tokenService.setTokens(res.accessToken, res.refreshToken);
        })
      );
  }

  // Auth
  public verifiCode(credentials: RequestCode) {
    return this.wrapper.handleRequest(this.http.post<any>(`${this.urlBase}/Auth/verify-code`, credentials))
      .pipe(
        tap(res => this.tokenService.setTokens(res.accessToken, res.refreshToken)),
        switchMap(() => this.getMe()),
        tap(user => this.userStore.setUser(user.data)),
      );
  }

  public refresh(refreshToken: RefreshRequest) {
    return this.http.post<ResponseToken>(`${this.urlBase}/Auth/refresh`, refreshToken)
      .pipe(
        tap(pair => {
          // Actualiza access token y refresh token
          this.tokenService.setTokens(pair.accessToken, pair.refreshToken);
        })
      );
  }

  public getMe() {
    return this.http.get<ApiResponse<UserMe>>(`${this.urlBase}/user/me`)
  }

  logout(refreshToken: RefreshRequest): Observable<void> {
    return this.http.post<void>(`${this.urlBase}/revoke`, { refreshToken })
      .pipe(
        tap(() =>
          this.tokenService.removeAll()
        )
      );
  }


  cachePendingEmail(email: string, id: string) {
    sessionStorage.setItem('pending_login_email', email);
    sessionStorage.setItem('pending_login_id', id);

  }
  getPendingEmail(): string | null {
    return sessionStorage.getItem('pending_login_email');
  }
  getPendingUserId(): string | null {
    return sessionStorage.getItem('pending_login_id');
  }
}

