  @ContentChild('cardContent', { static: false }) customCardTemplate?: TemplateRef<any>;

  currentPage = 1;
  pagedItems: CardItem[] = [];

  ngOnInit() {
    this.updatePagedItems();
  }

  updatePagedItems() {
    const start = (this.currentPage - 1) * this.pageSize;
    const end = start + this.pageSize;
    this.pagedItems = this.items.slice(start, end);
  }

  nextPage() {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.updatePagedItems();
    }
  }

  prevPage() {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.updatePagedItems();
    }
  }

  get totalPages(): number {
    return Math.ceil(this.items.length / this.pageSize);
  }

  onCardClicked(item: CardItem) {
    this.cardClick.emit(item);
  }

  // Método para obtener la variant de cada tarjeta
  getCardVariant(item: CardItem): CardVariant {
    return item.variant || this.variant;
  }
}

import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatDividerModule } from '@angular/material/divider';
import { MatBadgeModule } from '@angular/material/badge';

export interface Notification {
  id: string;
  title: string;
  message: string;
  type: 'info' | 'warning' | 'error' | 'success';
  timestamp: Date;
  read: boolean;
  actionText?: string;
  actionIcon?: string;
}

@Component({
  selector: 'app-generic-notification',
  imports: [ CommonModule,MatIconModule,MatButtonModule,MatDividerModule,MatBadgeModule],
  templateUrl: './generic-notification.component.html',
  styleUrl: './generic-notification.component.css'
})
export class GenericNotificationComponent  implements OnInit {
  @Input() visible: boolean = false;
  @Output() close = new EventEmitter<void>();
  @Output() notificationAction = new EventEmitter<Notification>();

  notifications: Notification[] = [];
  hasMoreNotifications: boolean = false;

  get unreadCount(): number {
    return this.notifications.filter(n => !n.read).length;
  }

  ngOnInit() {
    // Datos de ejemplo - reemplazar con tu servicio
    this.loadNotifications();
  }

  loadNotifications() {
    this.notifications = [
      {
        id: '1',
        title: 'Nueva actualización disponible',
        message: 'Una nueva versión del sistema está disponible. Se recomienda actualizar para obtener las últimas características.',
        type: 'info',
        timestamp: new Date(Date.now() - 5 * 60 * 1000), // 5 minutos atrás
        read: false,
        actionText: 'Actualizar ahora',
        actionIcon: 'system_update'
      },
      {
        id: '2',
        title: 'Advertencia de seguridad',
        message: 'Se detectaron múltiples intentos de acceso fallidos desde una dirección IP desconocida.',
        type: 'warning',
        timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000), // 2 horas atrás
        read: false,
        actionText: 'Ver detalles',
        actionIcon: 'security'
      },
      {
        id: '3',
        title: 'Tarea completada exitosamente',
        message: 'La sincronización de datos se completó correctamente. Todos los registros están actualizados.',
        type: 'success',
        timestamp: new Date(Date.now() - 1 * 24 * 60 * 60 * 1000), // 1 día atrás
        read: true
      },
      {
        id: '4',
        title: 'Error en el procesamiento',
        message: 'No se pudo procesar el archivo adjunto. Verifica el formato e intenta nuevamente.',
        type: 'error',
        timestamp: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000), // 2 días atrás
        read: true,
        actionText: 'Reintentar',
        actionIcon: 'refresh'
      }
    ];
  }

  loadMore() {
    console.log('Loading more notifications...');
  }

  markAllAsRead() {
    this.notifications.forEach(n => n.read = true);
  }

  clearAll() {
    this.notifications = [];
  }

  toggleRead(notification: Notification) {
    notification.read = !notification.read;
  }

  deleteNotification(id: string) {
    this.notifications = this.notifications.filter(n => n.id !== id);
  }

  onNotificationAction(notification: Notification) {
    this.notificationAction.emit(notification);
  }

  getNotificationIcon(type: string): string {
    const icons = {
      info: 'info',
      warning: 'warning',
      error: 'error',
      success: 'check_circle'
    };
    return icons[type as keyof typeof icons] || 'notifications';
  }

  getTimeAgo(timestamp: Date): string {
    const now = new Date();
    const diff = now.getTime() - timestamp.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);

    if (days > 0) return `${days}d`;
    if (hours > 0) return `${hours}h`;
    if (minutes > 0) return `${minutes}m`;
    return 'Ahora';
  }

  trackByFn(index: number, item: Notification): string {
    return item.id;
  }
}

import { CommonModule } from '@angular/common';
import { Component, ContentChildren, EventEmitter, Input, Output, QueryList, TemplateRef, ViewChild } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatIconModule } from '@angular/material/icon';
import { MatInputModule } from '@angular/material/input';
import { MatSlideToggle } from '@angular/material/slide-toggle';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';
import { ActivatedRoute, Router } from '@angular/router';
import Swal from 'sweetalert2';
import { MatSelectModule } from "@angular/material/select";
import { MatDividerModule } from "@angular/material/divider";
import { MatMenuModule } from "@angular/material/menu";
import { MatCheckboxModule } from "@angular/material/checkbox";
import { MatPaginator, MatPaginatorModule } from '@angular/material/paginator';
import { MatTooltipModule } from '@angular/material/tooltip';

@Component({
  selector: 'app-generic-table',
  imports: [
    CommonModule,
    MatSlideToggle,
    MatCardModule,
    MatTableModule,
    MatIconModule,
    MatButtonModule,
    MatFormFieldModule,
    FormsModule,
    MatInputModule,
    MatSelectModule,
    MatDividerModule,
    MatMenuModule,
    MatPaginatorModule,
    MatTooltipModule
  ],
  templateUrl: './generic-table.component.html',
  styleUrl: './generic-table.component.css'
})
export class GenericTableComponent {
  @Input() title: string = '';
  @Input() displayedColumns: string[] = [];
  @Input() columns: { key: string, label: string }[] = [];
  @Input() buttonSave: boolean = true;

  @Output() onEdit = new EventEmitter<any>();
  @Output() onDelete = new EventEmitter<any>();
  @Output() onToggleStatus = new EventEmitter<any>();
  @Output() onCreate = new EventEmitter<void>();

  @Input() customTemplates: { [key: string]: TemplateRef<any> } = {};

  // Usamos MatTableDataSource para la paginación y ordenamiento
  dataSource = new MatTableDataSource<any>();

  @ViewChild(MatPaginator) paginator!: MatPaginator;
  /**
   *
   */
  constructor(
    private router: Router,
    private route: ActivatedRoute
  ) { }

  @Input() set dataSourceInput(data: any[]) {
    this.dataSource.data = data || [];
  }

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }

  emitEdit(item: any) {
    this.onEdit.emit(item);
  }


  emitDelete(item: any) {
    Swal.fire({
      icon: 'warning',
      title: '¿Eliminar registro?',
      text: 'Esta acción no se puede deshacer. ¿Estás seguro?',
      showCancelButton: true,
      confirmButtonText: 'Sí, eliminar',
      cancelButtonText: 'Cancelar'
    }).then(result => {
      if (result.isConfirmed) {
        this.onDelete.emit(item);
      }
    });
  }

  emitToggle(item: any) {
    const isActivating = item.isDeleted;

    Swal.fire({
      icon: 'question',
      title: `${isActivating ? '¿Activar' : '¿Desactivar'} registro?`,
      text: `Estás a punto de ${isActivating ? 'activar' : 'desactivar'} este elemento.`,
      showCancelButton: true,
      confirmButtonText: `Sí, ${isActivating ? 'activar' : 'desactivar'}`,
      cancelButtonText: 'Cancelar'
    }).then(result => {
      if (result.isConfirmed) {
        item.isDeleted = !item.isDeleted;  // invierte el valor

      this.onToggleStatus.emit(item);

      // Forzar actualización tabla:
      this.dataSource.data = [...this.dataSource.data];
        // Swal.fire(
        //   isActivating ? 'Activado' : 'Desactivado',
        //   `El elemento ha sido ${isActivating ? 'activado' : 'desactivado'} correctamente.`,
        //   'success'
        // );
      }
    });
  }

  emitCreate() {
    this.router.navigate(['create'], { relativeTo: this.route });
    this.onCreate.emit();
  }
}

