    private snackbarService: SnackbarService,
    private dataService: DataService
  ) { }

  ngOnInit(): void {
    this.cargarData()

  }

  cargarData() {
    this.dataService.usuarios$.subscribe(data => this.listUsers = data);
    // Carga inicial de los datos dinámicos
    this.dataService.getUsers();

    this.dataService.roles$.subscribe(data => this.listRoles = data);
    this.dataService.getRoles();
  }
  openModal(user: UserList) {
    this.personService.ObtenerPorId('Person', user.personId).subscribe((response) => {
      let item = response.data;
      this.dialog.open(TargetPersonComponent, {
        disableClose: true,
        width: '800px',
        maxHeight: '80vh',
        data: {
          title: item ? 'Editar' : 'Crear',
          item
        }
      });

      console.log('Datos cargados:', item);
    });
  }


  add(Form: UserCreate, id?: number) {
    if (id) {
      this.apiService.update('User', Form).subscribe(() => {
        this.cargarData();
        this.snackbarService.showSuccess();
      })
    }
    else {
      this.apiService.Crear('User', Form).subscribe(() => {
        this.cargarData();
        this.snackbarService.showSuccess();
      })
    }
  }

  save(data?: UserCreate) {
  }

  delete(item: any) {
    this.apiService.delete("Form", item.id).subscribe(() => {
      this.snackbarService.showInfo('Formulario eliminado con éxito')
      this.cargarData();
    })
  }
  toggleIsActive(item: any) {
    this.apiService.deleteLogic('User', item.id).subscribe(() => {
      this.snackbarService.showSuccess("Estado actualizado con éxito");
    })
  }


  openUserRolesModal(user: UserList | any) {
    const fields = [
      // Campo oculto con el id del usuario
      { name: 'id', value: user.id || 0, hidden: true },

      // Campo de solo lectura para mostrar el nombre
      { name: 'name', label: 'Nombre Usuario', type: 'text', value: user.namePerson || '', readonly: true },

      // Roles como checkboxes
      ...this.listRoles.map(role => ({
        name: 'role_' + role.id,
        label: role.name,
        type: 'checkbox',
        value: user.roles.some((r: any )=> r.id === role.id) // <-- aquí se marca si ya tiene ese rol
      }))
    ];


    const dialogRef = this.dialog.open(GenericFormComponent, {
      width: '400px',
      disableClose: true,
      data: {
        title: 'Roles para usuario',
        item: user,
        fields,
        replaceBaseFields: true
      }
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        const selectedRoleIds = Object.entries(result)
          .filter(([key, checked]) => checked && key.startsWith('role_'))
          .map(([key]) => parseInt(key.replace('role_', ''), 10));

        const data: UserRolCreateAll = {
          userId: user.id,
          rolesId: selectedRoleIds,
        };

        this.userRolesService.saveAllRoles(data).subscribe(() => {
          this.snackbarService.showSuccess();
          this.cargarData();
        });
      }
    });
  }


  viewMode: 'grid' | 'list' = 'grid';


  changeViewMode(event: any): void {
    this.viewMode = event.value;
  }

  savePerson(){
    this.router.navigate(['createUser'], { relativeTo: this.route });
  }
}

import { AuthService } from './../../../../../core/Services/auth/auth-service.service';
import { Component, signal, computed, effect, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
import { RequestCode } from '../../../../../core/Models/auth.models';
import { MenuCreateService } from '../../../../../core/Services/shared/menu-create.service';


@Component({
  selector: 'app-login-code',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login-code.component.html',
  styleUrl: './login-code.component.css'
})
export class LoginCodeComponent implements OnInit {
  codeInputs = signal([
    { value: '' },
    { value: '' },
    { value: '' },
    { value: '' },
    { value: '' }
  ]);

  email?: string = '';

  constructor(private router: Router,
    private authService: AuthService,
    private menu: MenuCreateService,

  ) {
    // Effect para monitorear cambios en el código completo
    effect(() => {
      if (this.isCodeComplete()) {
        console.log('Código completo:', this.fullCode());
      }
    });
  }
  ngOnInit(): void {
    this.email = this.authService.getPendingEmail() ?? '';
  }

  // Señal computada para verificar si el código está completo
  isCodeComplete = computed(() => {
    return this.codeInputs().every(input => input.value.length === 1);
  });



  // Señal computada para obtener el código completo
  fullCode = computed(() => {
    return this.codeInputs().map(input => input.value).join('');
  });



  onInputChange(index: number, event: Event): void {
    const target = event.target as HTMLInputElement;
    const value = target.value.replace(/[^0-9]/g, ''); // Solo números

    // Actualizar la señal
    this.codeInputs.update(inputs => {
      const newInputs = [...inputs];
      newInputs[index] = { value: value.slice(-1) }; // Solo el último dígito
      return newInputs;
    });

    // Mover al siguiente input si hay un valor
    if (value && index < 4) {
      const nextInput = target.parentElement?.children[index + 1] as HTMLInputElement;
      if (nextInput) {
        nextInput.focus();
      }
    }
  }

  onKeyDown(index: number, event: KeyboardEvent): void {
    const target = event.target as HTMLInputElement;

    // Backspace: limpiar y mover al anterior
    if (event.key === 'Backspace' && !target.value && index > 0) {
      const prevInput = target.parentElement?.children[index - 1] as HTMLInputElement;
      if (prevInput) {
        this.codeInputs.update(inputs => {
          const newInputs = [...inputs];
          newInputs[index - 1] = { value: '' };
          return newInputs;
        });
        prevInput.focus();
      }
    }

    // Arrow keys para navegación
    if (event.key === 'ArrowLeft' && index > 0) {
      const prevInput = target.parentElement?.children[index - 1] as HTMLInputElement;
      if (prevInput) prevInput.focus();
    }

    if (event.key === 'ArrowRight' && index < 4) {
      const nextInput = target.parentElement?.children[index + 1] as HTMLInputElement;
      if (nextInput) nextInput.focus();
    }
  }

  onPaste(event: ClipboardEvent): void {
    event.preventDefault();
    const pastedData = event.clipboardData?.getData('text') || '';
    const digits = pastedData.replace(/[^0-9]/g, '').slice(0, 5);

    if (digits.length > 0) {
      this.codeInputs.update(inputs => {
        return inputs.map((input, index) => ({
          value: digits[index] || ''
        }));
      });

      // Enfocar el último input con valor o el primero vacío
      const lastFilledIndex = Math.min(digits.length - 1, 4);
      const targetInput = event.target as HTMLInputElement;
      const nextInput = targetInput.parentElement?.children[lastFilledIndex] as HTMLInputElement;
      if (nextInput) {
        nextInput.focus();
      }
    }
  }

  onContinue(): void {
    if (this.isCodeComplete()) {
      // console.log('Verificando código:', this.fullCode());
      // this.router.navigate(['/auth/']);
      // alert(`Código ingresado: ${this.fullCode()}`);
      var requestCode: RequestCode = {
        userId: Number(this.authService.getPendingUserId()),
        code: this.fullCode()
      }
      this.authService.verifiCode(requestCode).subscribe({
        next: (response) => {
          if (response) {
            // ✅ Caso exitoso
            this.menu.reload();
            this.router.navigate(['dashboard']);

            // Opcional: mostrar notificación
            console.log(response.message);
          } else {
            // ❌ La API respondió 200 pero con error lógico
            // console.warn('Login fallido:', response?.message);
            // alert(response.message);
          }
        },
        error: (error) => {
          // ❌ Error de red o statusCode >= 400
          console.error('Error al iniciar sesión:', error);
          // alert('Ocurrió un error inesperado, intenta de nuevo.');
        }
      });
    }
  }

  reenviarCodigo() {
    Swal.fire("Reenvio exitoso! prueba el nuevo codigo");
  }




  onResend(): void {
    // Limpiar todos los inputs
    this.codeInputs.set([
      { value: '' },
      { value: '' },
      { value: '' },
      { value: '' },
      { value: '' }
    ]);

    console.log('Reenviando código...');
    alert('Código reenviado exitosamente');

    // Enfocar el primer input
    setTimeout(() => {
      const firstInput = document.querySelector('.code-digit') as HTMLInputElement;
      if (firstInput) firstInput.focus();
    }, 100);
  }
}

