.verification-card {
  display: flex;
  max-width: 900px;
  width: 90%;
  min-height: 500px;
  border-radius: 20px;
  overflow: hidden;
  backdrop-filter: blur(12px);
  background: rgba(255, 255, 255, 0.1);
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.3);
}

/* Lado de la imagen */
.illustration-section {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #fff;
}

.illustration-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Lado del formulario */
.form-section {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(12px);
}

.form-content {
