  padding: 2rem;
  width: 100%;
  max-width: 350px;
}

.title {
  color: #fff;
  font-size: 1.8rem;
  font-weight: bold;
  margin-bottom: 1rem;
  text-shadow: 1px 1px 4px rgba(0,0,0,0.3);
}

.instruction {
  color: rgba(255, 255, 255, 0.85);
  font-size: 1rem;
  margin-bottom: 2rem;
}

/* Ícono de éxito */
.success-icon {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 1.5rem;
}

.success-icon img {
  width: 80px;
  height: 80px;
  object-fit: contain;
}

/* Grupos de formulario */
.form-group {
  margin-bottom: 1.5rem;
  text-align: left;
}

/* Label del campo */
.field-label {
  display: block;
  color: rgba(255, 255, 255, 0.9);
  font-size: 0.9rem;
  margin-bottom: 0.5rem;
  font-weight: 500;
}

/* Container para input con botón de toggle */
.password-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

/* Input de contraseña */
.form-input {
  width: 100%;
  padding: 16px 50px 16px 20px;
  border: none;
  border-radius: 12px;
  font-size: 16px;
  background: rgba(255, 255, 255, 0.8);
  color: #333;
  transition: all 0.3s ease;
  border: 2px solid transparent;
}

.form-input:focus {
  outline: none;
  background: #749BC2;
  border-color: rgba(255, 255, 255, 0.5);
  box-shadow: 0 0 0 3px rgba(0, 119, 182, 0.1);
}

.form-input::placeholder {
  color: rgba(0, 0, 0, 0.4);
}

/* Botón de toggle password */
.password-toggle {
  position: absolute;
  right: 15px;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: rgba(0, 0, 0, 0.6);
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.password-toggle:hover {
  color: rgba(0, 0, 0, 0.8);
  background: rgba(0, 0, 0, 0.05);
}

.password-toggle:focus {
  outline: none;
  box-shadow: 0 0 0 2px rgba(0, 119, 182, 0.3);
}

/* Botón continuar */
.continue-btn {
  background: linear-gradient(135deg, #0077B6 0%, #003450 100%);
  color: white;
  padding: 12px 20px;
  border-radius: 25px;
  border: none;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 0.5rem;
  transition: all 0.3s ease;
  width: 100%;
  position: relative;
}

.continue-btn:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 119, 182, 0.3);
}

.continue-btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

/* Botón reenviar */
.resend-btn {
  background: transparent;
  border: none;
  color: #0d40ad;
  font-size: 0.9rem;
  cursor: pointer;
  opacity: 0.8;
  padding: 8px;
  border-radius: 4px;
  transition: all 0.2s ease;
}

.resend-btn:hover:not(:disabled) {
  opacity: 1;
  background: rgba(255, 255, 255, 0.1);
}

.resend-btn:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

/* Mensajes de error */
.error-message {
  color: #ff6b6b;
  font-size: 12px;
  margin-top: 0.5rem;
  text-align: left;
  background: rgba(255, 107, 107, 0.15);
  padding: 0.5rem;
  border-radius: 8px;
  border-left: 3px solid #ff6b6b;
  backdrop-filter: blur(4px);
}

/* Loading state */
.btn-loading::after {
  content: '';
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translateY(-50%);
  width: 16px;
  height: 16px;
  border: 2px solid transparent;
  border-top-color: #ffffff;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: translateY(-50%) rotate(0deg); }
  100% { transform: translateY(-50%) rotate(360deg); }
}

/* Responsive */
@media (max-width: 768px) {
  .verification-card {
    flex-direction: column;
    max-width: 500px;
    min-height: auto;
  }
  
  .illustration-section {
    min-height: 250px;
    order: 0;
  }
  
  .form-section {
    order: 1;
    padding: 1rem;
  }
  
  .form-content {
    padding: 1.5rem;
  }
  
  .title {
    font-size: 1.5rem;
  }
}

@media (max-width: 480px) {
  .verification-card {
    width: 95%;
    border-radius: 16px;
  }
  
  .form-content {
    padding: 1rem;
    max-width: 100%;
  }
  
  .illustration-section {
    min-height: 200px;
  }
  
  .success-icon img {
    width: 60px;
    height: 60px;
  }
  
  .form-input {
    padding: 14px 45px 14px 16px;
  }
  
  .continue-btn {
    padding: 12px 16px;
  }
}

.storybook-page {
  margin: 0 auto;
  padding: 48px 20px;
  max-width: 600px;
  color: #333;
  font-size: 14px;
  line-height: 24px;
  font-family: 'Nunito Sans', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

.storybook-page h2 {
  display: inline-block;
  vertical-align: top;
  margin: 0 0 4px;
  font-weight: 700;
  font-size: 32px;
  line-height: 1;
}

.storybook-page p {
  margin: 1em 0;
}

.storybook-page a {
  color: inherit;
}

.storybook-page ul {
  margin: 1em 0;
  padding-left: 30px;
}

.storybook-page li {
