import { Component, signal, computed, effect } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
import { RequestCode } from '../../../../../core/Models/auth.models';
import { AuthService } from '../../../../../core/Services/auth/auth-service.service';
import { MenuCreateService } from '../../../../../core/Services/shared/menu-create.service';


@Component({
  selector: 'app-code-verification',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: `./recuperation-code.component.html`,
  styleUrl: `./recuperation-code.component.css`
})
export class RecuperationCodeComponent {
   codeInputs = signal([
    { value: '' },
    { value: '' },
    { value: '' },
    { value: '' },
    { value: '' }
  ]);

    constructor(private router: Router,
    private authService: AuthService,
    private menu: MenuCreateService,
    ) {
    // Effect para monitorear cambios en el código completo
    effect(() => {
      if (this.isCodeComplete()) {
        console.log('Código completo:', this.fullCode());
      }
    });
  }

  // Señal computada para verificar si el código está completo
  isCodeComplete = computed(() => {
    return this.codeInputs().every(input => input.value.length === 1);
  });



  // Señal computada para obtener el código completo
  fullCode = computed(() => {
    return this.codeInputs().map(input => input.value).join('');
  });



  onInputChange(index: number, event: Event): void {
    const target = event.target as HTMLInputElement;
    const value = target.value.replace(/[^0-9]/g, ''); // Solo números

    // Actualizar la señal
    this.codeInputs.update(inputs => {
      const newInputs = [...inputs];
      newInputs[index] = { value: value.slice(-1) }; // Solo el último dígito
      return newInputs;
    });

    // Mover al siguiente input si hay un valor
    if (value && index < 4) {
      const nextInput = target.parentElement?.children[index + 1] as HTMLInputElement;
      if (nextInput) {
        nextInput.focus();
      }
    }
  }

  onKeyDown(index: number, event: KeyboardEvent): void {
    const target = event.target as HTMLInputElement;

    // Backspace: limpiar y mover al anterior
    if (event.key === 'Backspace' && !target.value && index > 0) {
      const prevInput = target.parentElement?.children[index - 1] as HTMLInputElement;
      if (prevInput) {
        this.codeInputs.update(inputs => {
          const newInputs = [...inputs];
          newInputs[index - 1] = { value: '' };
          return newInputs;
        });
        prevInput.focus();
      }
    }

    // Arrow keys para navegación
    if (event.key === 'ArrowLeft' && index > 0) {
      const prevInput = target.parentElement?.children[index - 1] as HTMLInputElement;
      if (prevInput) prevInput.focus();
    }

    if (event.key === 'ArrowRight' && index < 4) {
      const nextInput = target.parentElement?.children[index + 1] as HTMLInputElement;
      if (nextInput) nextInput.focus();
    }
  }

  onPaste(event: ClipboardEvent): void {
    event.preventDefault();
    const pastedData = event.clipboardData?.getData('text') || '';
    const digits = pastedData.replace(/[^0-9]/g, '').slice(0, 5);

    if (digits.length > 0) {
      this.codeInputs.update(inputs => {
        return inputs.map((input, index) => ({
          value: digits[index] || ''
        }));
      });

      // Enfocar el último input con valor o el primero vacío
      const lastFilledIndex = Math.min(digits.length - 1, 4);
      const targetInput = event.target as HTMLInputElement;
      const nextInput = targetInput.parentElement?.children[lastFilledIndex] as HTMLInputElement;
      if (nextInput) {
        nextInput.focus();
      }
    }
  }

  onContinue(): void {
    if (this.isCodeComplete()) {
      // console.log('Verificando código:', this.fullCode());
      // this.router.navigate(['/auth/']);
      // alert(`Código ingresado: ${this.fullCode()}`);
      var requestCode: RequestCode = {
        userId: Number(this.authService.getPendingUserId()),
        code: this.fullCode()
      }
      this.authService.verifiCode(requestCode).subscribe({
        next: (response) => {
          if (response) {
            // ✅ Caso exitoso
            this.menu.reload();
            this.router.navigate(['dashboard']);

            // Opcional: mostrar notificación
            console.log(response.message);
          } else {
            // ❌ La API respondió 200 pero con error lógico
            console.warn('Login fallido:', response);
            // alert(response.message);
          }
        },
        error: (error) => {
          // ❌ Error de red o statusCode >= 400
          console.error('Error al iniciar sesión:', error);
          // alert('Ocurrió un error inesperado, intenta de nuevo.');
        }
      });
      console.log('Verificando código:', this.fullCode());
      this.router.navigate(['/auth/new-password']);
      alert(`Código ingresado: ${this.fullCode()}`);
    }
  }
  reenviarCodigo() {
      Swal.fire("Reenvio exitoso! prueba el nuevo codigo");
    }

  onResend(): void {
    // Limpiar todos los inputs
    this.codeInputs.set([
      { value: '' },
      { value: '' },
      { value: '' },
      { value: '' },
      { value: '' }
    ]);

    console.log('Reenviando código...');
    alert('Código reenviado exitosamente');

    // Enfocar el primer input
    setTimeout(() => {
      const firstInput = document.querySelector('.code-digit') as HTMLInputElement;
      if (firstInput) firstInput.focus();
    }, 100);
  }
}

import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SnackbarService } from '../../../core/Services/snackbar/snackbar.service';

@Component({
  selector: 'app-seccion-password',
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './seccion-password.component.html',
  styleUrl: './seccion-password.component.css'
})
export class SeccionPasswordComponent {
  private fb = inject(FormBuilder);

  passwordForm: FormGroup;
  showCurrentPassword = false;
  showNewPassword = false;
  showConfirmPassword = false;

  constructor(
        private snackbarService: SnackbarService,
    
  ) {
     
    this.passwordForm = this.fb.group({
      contrasenaActual: ['', [Validators.required, Validators.minLength(6)]],
      nuevaContrasena: ['', [Validators.required, Validators.minLength(6)]],
      confirmarContrasena: ['', [Validators.required]]
    }, { validators: this.passwordMatchValidator });
  }

  passwordMatchValidator(form: FormGroup) {
    const nuevaContrasena = form.get('nuevaContrasena');
    const confirmarContrasena = form.get('confirmarContrasena');
    
    if (nuevaContrasena && confirmarContrasena && nuevaContrasena.value !== confirmarContrasena.value) {
      confirmarContrasena.setErrors({ passwordMismatch: true });
      return { passwordMismatch: true };
    }
    
    if (confirmarContrasena?.hasError('passwordMismatch')) {
      delete confirmarContrasena.errors!['passwordMismatch'];
      if (Object.keys(confirmarContrasena.errors!).length === 0) {
        confirmarContrasena.setErrors(null);
      }
    }
    
    return null;
  }

  togglePasswordVisibility(field: string) {
    switch (field) {
      case 'current':
        this.showCurrentPassword = !this.showCurrentPassword;
        break;
      case 'new':
        this.showNewPassword = !this.showNewPassword;
        break;
      case 'confirm':
        this.showConfirmPassword = !this.showConfirmPassword;
        break;
    }
  }

  onSubmit() {
    if (this.passwordForm.valid) {
      console.log('Contraseña cambiada:', this.passwordForm.value);
      // Aquí iría la lógica para cambiar la contraseña
        this.snackbarService.showSuccess("Contraseña cambiada exitosamente");

      this.passwordForm.reset();
    } else {
      console.log('Formulario inválido');
    }
  }

  getFieldError(fieldName: string): string {
    const field = this.passwordForm.get(fieldName);
    if (field?.errors && field.touched) {
      if (field.errors['required']) return 'Este campo es requerido';
      if (field.errors['minlength']) return 'Mínimo 6 caracteres';
      if (field.errors['passwordMismatch']) return 'Las contraseñas no coinciden';
    }
    return '';
  }
}

import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MatIconModule } from '@angular/material/icon';
import { GenericCredincialsComponent } from '../../../shared/components/generic-credincials/generic-credincials.component';

@Component({
  selector: 'app-seccion-perfil',
  standalone: true,
  // AGREGAR el componente genérico a las importaciones
  imports: [CommonModule, ReactiveFormsModule, MatIconModule, GenericCredincialsComponent],
  templateUrl: './seccion-perfil.component.html',
  styleUrl: './seccion-perfil.component.css'
})
export class SeccionPerfilComponent {
  isModalOpen = false;
  private fb = inject(FormBuilder);
  private router = inject(Router);

  perfilForm: FormGroup;

  constructor() {
    this.perfilForm = this.fb.group({
      nombre: ['Juan Carlos', Validators.required],
      primerApellido: ['García', Validators.required],
      segundoApellido: ['López'],
      email: ['juan.garcia@email.com', [Validators.required, Validators.email]],
      telefono: ['+57 301 234 5678', [Validators.pattern(/^\+?[\d\s-()]+$/)]]
    });
  }

  onSubmit() {
    if (this.perfilForm.valid) {
      console.log('Datos actualizados:', this.perfilForm.value);
      alert('Perfil actualizado exitosamente');
    } else {
      console.log('Formulario inválido');
      this.markAllFieldsAsTouched();
    }
  }

  abrirModal() {
    this.isModalOpen = true;
  }

  cerrarModal() {
    this.isModalOpen = false;
  }

  onValidacionExitosa(password: string) {
    console.log('Contraseña validada correctamente:', password);
    this.cerrarModal();
    // Aquí puedes habilitar la edición del formulario o hacer lo que necesites
    alert('¡Credenciales validadas! Ahora puedes editar tu perfil.');
  }

  private markAllFieldsAsTouched() {
    Object.keys(this.perfilForm.controls).forEach(key => {
      this.perfilForm.get(key)?.markAsTouched();
    });
  }

  getFieldError(fieldName: string): string {
    const field = this.perfilForm.get(fieldName);
    if (field?.errors && field.touched) {
      if (field.errors['required']) return 'Este campo es requerido';
      if (field.errors['email']) return 'Ingresa un email válido';
      if (field.errors['pattern']) return 'Formato de teléfono inválido';
    }
    return '';
  }

  resetForm() {
    this.perfilForm.reset({
      nombre: 'Juan Carlos',
      primerApellido: 'García',
      segundoApellido: 'López',
      email: 'juan.garcia@email.com',
      telefono: '+57 301 234 5678'
    });
  }
}

