.sidebar-container {
  height: 100vh;
  width: 100%;
}

.sidebar-drawer {
  background: aquamarine !important;
}

mat-sidenav-container.mat-drawer-container.mat-sidenav-container.sidebar-container.mat-drawer-transition {
  background-color: transparent;
}

.sidebar {
  width: 280px;
  background-color: #F6F6FB;
  color: #2B2F42 !important;
  overflow-y: auto;
  overflow-x: hidden;
  border-right: none;
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE */
  border-top-right-radius: 7.2vh;
  border-bottom-right-radius: 7.2vh;
}

.sidebar::-webkit-scrollbar {
  display: none;
}

.sidebar-header {
  padding: 20px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);

  .logo {
    margin: 0;
    font-size: 30px;
    font-weight: 600;
    display: flex;
    align-items: baseline;
    gap: 8px;

    .logo-text {
      color: #2B2F42 !important;
    }

    .version {
      font-size: 12px;
      background: rgba(145, 200, 228, 0.4);
      padding: 2px 8px;
      border-radius: 12px;
      font-weight: 400;
      color: #013655;
    }
  }
}

.sidebar-content {
  padding: 20px 0;
}

.navigation-menu {
  .menu-group {
    margin-bottom: 24px;

    .group-title {
      padding: 0 20px 12px 20px;
      font-size: 18px;
      font-weight: 600;
      text-transform: uppercase;
      letter-spacing: 0.5px;
      color: #013655;
    }

    .group-items {
      .menu-item {
        position: relative;
        margin: 0 12px 4px 12px;
        border-radius: 8px;
        transition: all 0.3s ease;
        cursor: pointer;

        &:hover {
          background: rgba(145, 200, 228, 0.5);
          transform: translateX(4px);
        }

        &.active {
          background: rgba(145, 200, 228, 0.5);

          &::before {
            content: '';
            position: absolute;
            left: -12px;
            top: 0;
            height: 100%;
            width: 3px;
            background: #013655;
            border-radius: 0 3px 3px 0;
          }
        }

        &:not(.collapsible) {
          display: flex;
          align-items: center;
          padding: 12px 16px;
          gap: 12px;

          .item-icon {
            font-size: 26px; /* aumentado */
            width: 26px;
            height: 26px;
            color: #013655 !important;
          }

          .item-text {
            font-size: 14px;
            font-weight: 500;
            color: #2B2F42;
          }
        }

        &.collapsible {
          .collapse-header {
            display: flex;
            align-items: center;
            justify-content: space-between;
            padding: 12px 16px;
            border-radius: 8px;
            transition: all 0.3s ease;
            color: #2B2F42 !important;

            &:hover {
              background: rgba(145, 200, 228, 0.6);
            }

            &.active {
              background: rgba(145, 200, 228, 0.6);
            }

            .collapse-content {
              display: flex;
              align-items: center;
              gap: 12px;

              .item-icon {
                font-size: 26px; /* aumentado */
                width: 26px;
                height: 26px;
                color: #013655;
              }

              .item-text {
                font-size: 14px;
                font-weight: 500;
                color: #2B2F42;
              }
            }

            .expand-icon {
              font-size: 22px; /* un poco más grande */
              transition: transform 0.3s ease;

              &.rotated {
                transform: rotate(180deg);
              }
            }
          }

          .collapse-content-wrapper {
            max-height: 0;
            overflow: hidden;
            transition: max-height 0.3s ease;

            &.expanded {
              max-height: 400px;
            }

            .collapse-items {
              padding: 8px 0 0 0;

              .sub-menu-item {
                display: flex;
                align-items: center;
                justify-content: space-between;
                padding: 10px 16px 10px 48px;
                margin: 2px 0;
                border-radius: 6px;
                transition: all 0.3s ease;
                cursor: pointer;

                &:hover {
                  background: rgba(145, 200, 228, 0.5);
                  transform: translateX(4px);
                }

                &.active {
                  background: rgba(179, 255, 0, 0.8);

                  .sub-item-text {
                    color: #013655;
                  }
                }

                .sub-item-text {
                  font-size: 13px;
                  font-weight: 400;
                  color: #2B2F42;
                }

                .arrow-icon {
                  font-size: 16px;
                  color: #013655;
                }
              }
            }
          }
        }
      }
    }
  }
}

.main-content {
  background: #f5f7fa;
  min-height: 100vh;
}

.mobile-toggle {
  position: fixed;
  bottom: 20px;
  right: 20px;
  z-index: 1000;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

@media (max-width: 768px) {
  .mat-drawer {
    position: absolute !important;
    z-index: 1050;
    width: 100% !important;
  }

  .mat-drawer-opened + .main-content {
    pointer-events: none;
  }
}

.mat-drawer-container {
  height: 100%;
}

.main-content {
  flex: 1;
  transition: margin-left 0.3s ease;
}

@media (max-width: 768px) {
  .mat-drawer {
    z-index: 1050 !important;
    width: 100% !important;
  }

  .mat-drawer-opened + .main-content {
    pointer-events: none;
  }
}

@media (max-width: 576px) {
  .sidebar-header {
    padding: 16px;

    .logo {
      font-size: 20px;

      .version {
        font-size: 10px;
        padding: 1px 6px;
      }
    }
  }

  .navigation-menu {
    .menu-group {
      .group-items {
        .menu-item {
          margin: 0 8px 4px 8px;

          &:not(.collapsible) {
            padding: 10px 12px;

            .item-icon {
              font-size: 20px; /* en móviles más pequeño */
              width: 20px;
              height: 20px;
            }

            .item-text {
              font-size: 13px;
            }
          }
        }
      }
    }
  }
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }

  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.menu-item {
  animation: slideIn 0.3s ease forwards;
}

.menu-item:focus-visible,
.collapse-header:focus-visible,
.sub-menu-item:focus-visible {
  outline: 2px solid rgba(145, 200, 228, 0.6);
  outline-offset: 2px;
}

