(req, next) => {
  const authService = inject(TokenService);
  const token = authService.getAccessToken();

  // if(req.context.get(CHECK_))

  if (token && authService.isAuthenticated()) {
    const cloned = req.clone({
      setHeaders: {
        Authorization: `Bearer ${token}`
      }
    });
    return next(cloned);
  }

  return next(req);
}

